import React, { useEffect, useState } from 'react';

function FloatingIcon() {

  useEffect(() => {
    const handleScroll = () => {
        const passedDiv = document.getElementById('ResumeDiv');
        const hiddenDiv = document.getElementById('SkillsDiv');
        const floatingIcon = document.getElementById('floating-icon');
        const scrollPosition = window.scrollY;
        console.log("1) ResumeDiv's top = " + passedDiv.offsetTop);
        console.log("2) ProjectDiv's Bottom = " + document.getElementById('ProjectDiv').offsetHeight + document.getElementById('ProjectDiv').offsetTop);
        console.log("3) SkillsDiv's top = " + hiddenDiv.offsetTop);
        console.log("scrollPosition = " + scrollPosition);

        const myElement = document.getElementById('SkillsDiv');
        const { top } = myElement.getBoundingClientRect();
        const marginTop = parseInt(window.getComputedStyle(myElement).marginTop, 10);
        const topPosition = top + marginTop;
        console.log("4) topPosition = " + topPosition);

        if (
        scrollPosition >= passedDiv.offsetTop &&
        scrollPosition <= hiddenDiv.offsetTop
        ) {
        floatingIcon.classList.add('active');
        } else {
        floatingIcon.classList.remove('active');
        }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div id="floating-icon">
      <img src="./kayak.svg" alt="Floating Icon" />
    </div>
  );
}

export default FloatingIcon;
